"use strict";exports.formatRelativeTime=function(r){const t=new Date(r).getTime(),e=Date.now();if(isNaN(t))return console.error("无效的时间戳"),null;const n=e-t;if(n<0)return"1分钟内";const o=6e4,f=36e5,i=24*f,u=30*i;if(n<o)return"1分钟内";if(n<f){return`${Math.floor(n/o)}分钟前`}if(n<i){return`${Math.floor(n/f)}小时前`}if(n<u){return`${Math.floor(n/i)}天前`}if(n<12*u){return`${Math.floor(n/u)}个月前`}return null};
